   	   AND SALARY_STEP_BY_RANK = #{ salaryStepByRank }

mapper.update:
UPDATE TBL_RANK_SALARY_STEP
   SET TBL_USER_AUTH = 'Y'
 WHERE COMPANY_NO = #{ companyNo }
   AND RANK = #{ rank }

mapper.select:
