mapper.select:
	SELECT
		   AUTHORITY_NO
	  FROM TBL_RANK_AUTH
	 WHERE COMPANY_NO = #{ companyNo }
   	   AND SALARY_STEP_BY_RANK = #{ salaryStepByRank }

mapper.update:
UPDATE TBL_RANK_SALARY_STEP
   SET TBL_USER_AUTH = 'Y'
 WHERE COMPANY_NO = #{ companyNo }
   AND RANK = #{ rank }

mapper.select:
SELECT
	   SALARY_STEP_BY_RANK
  FROM TBL_RANK_SALARY_STEP
 WHERE COMPANY_NO = #{ companyNo }
   AND RANK = #{ rankName }

mapper.delete:
	DELETE
	  FROM TBL_RANK_AUTH
 	 WHERE COMPANY_NO = #{ companyNo }
	   AND SALARY_STEP_BY_RANK IN

		   #{ item.salaryStepByRank }

insert.foreach:
	INTO TBL_RANK_AUTH
(
  COMPANY_NO
, SALARY_STEP_BY_RANK
, AUTHORITY_NO
, RANK_NAME
)
VALUES
(
  #{ companyNo }
, ${ item.salaryStepByRank }
, #{ authoryityNo }
, #{ rankName }
)

mapper.select:
SELECT
      A.TBL_USER_AUTH
    , A.ID_NO
 FROM TBL_EMPLOYEE A
WHERE A.MEMBER_NO = #{ memberNo }
  AND A.KOREAN_NAME = #{ userName }
  AND A.COMPANY_NO = #{ companyNo }

mapper.update:
UPDATE TBL_EMPLOYEE
   SET TBL_USER_AUTH = 'Y'
 WHERE COMPANY_NO = #{ companyNo }
   AND KOREAN_NAME = #{ userName }
   AND MEMBER_NO = #{ memberNo }

mapper.delete:
	DELETE
	  FROM TBL_USER_AUTH
 	 WHERE ID_NO = #{ idNo }

insert.foreach:
	INTO TBL_USER_AUTH
(
  ID_NO
, AUTHORITY_NO
)
VALUES
(
  #{ idNo }
, #{ item.authoryityNo }
)
